                                                                                                                                                                                                                    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
                                                                                                                                                                                                                                width: 100%;
                                                                                                                                                                                                                                            max-width: 400px;
                                                                                                                                                                                                                                                    }

                                                                                                                                                                                                                                                            h1 {
                                                                                                                                                                                                                                                                        text-align: center;
                                                                                                                                                                                                                                                                                    color: #333;
                                                                                                                                                                                                                                                                                                margin-bottom: 30px;
                                                                                                                                                                                                                                                                                                            font-size: 28px;
                                                                                                                                                                                                                                                                                                                    }

                                                                                                                                                                                                                                                                                                                            .form-group {
                                                                                                                                                                                                                                                                                                                                        margin-bottom: 20px;
                                                                                                                                                                                                                                                                                                                                                }

                                                                                                                                                                                                                                                                                                                                                        label {
                                                                                                                                                                                                                                                                                                                                                                    display: block;
                                                                                                                                                                                                                                                                                                                                                                                margin-bottom: 8px;
                                                                                                                                                                                                                                                                                                                                                                                            color: #555;
                                                                                                                                                                                                                                                                                                                                                                                                        font-weight: 600;
                                                                                                                                                                                                                                                                                                                                                                                                                    font-size: 14px;
                                                                                                                                                                                                                                                                                                                                                                                                                            }

                                                                                                                                                                                                                                                                                                                                                                                                                                    input[type="text"],
                                                                                                                                                                                                                                                                                                                                                                                                                                            input[type="email"],
                                                                                                                                                                                                                                                                                                                                                                                                                                                    input[type="password"] {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                width: 100%;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            padding: 12px;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        border: 1px solid #ddd;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    border-radius: 5px;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                font-size: 14px;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            transition: border-color 0.3s;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            input[type="text"]:focus,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    input[type="email"]:focus,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            input[type="password"]:focus {
